global I64_PACKED_LEN: u32 = 1;

impl Packable for bool {
    let N: u32 = BOOL_PACKED_LEN;

    #[inline_always]
    fn pack(self) -> [Field; Self::N] {
        [self as Field]
    }

    #[inline_always]
