    }
}

impl Packable for Field {
    let N: u32 = FIELD_PACKED_LEN;

    #[inline_always]
    fn pack(self) -> [Field; Self::N] {
        [self]
    }

    #[inline_always]
    fn unpack(fields: [Field; Self::N]) -> Self {
        fields[0]
    }
}

impl Packable for i8 {
    let N: u32 = I8_PACKED_LEN;

    #[inline_always]
    fn pack(self) -> [Field; Self::N] {
        [self as u8 as Field]
    }

    #[inline_always]
    fn unpack(fields: [Field; Self::N]) -> Self {
